import React, { PropTypes } from 'react';
import { createTestHook } from 'splunk-ui/util/testSupport';
import Box from 'splunk-ui/components/Box';
import ScreenReaderContent from 'splunk-ui/components/ScreenReaderContent';
import ComponentNav from './ComponentNav';
import css from './DocLayout.css';

const propTypes = {
    children: PropTypes.node,
    location: PropTypes.string,
};

function DocLayout(props) {
    const { children, location } = props;
    return (
        <Box className={css.main} {...createTestHook(__filename)}>
            <ScreenReaderContent>
                <a href="#navSkip" tabIndex={1}>Skip the navigation</a>
            </ScreenReaderContent>
            <ComponentNav current={location} />
            {children}
        </Box>
    );
}

export default DocLayout;
DocLayout.propTypes = propTypes;
