import React, { PropTypes } from 'react';
import { createTestHook } from 'splunk-ui/util/testSupport';
import Heading from 'splunk-ui/components/Heading';
import Markdown from 'splunk-ui/components/Markdown';
import Status from './Status';

const propTypes = {
    children: PropTypes.node,
    description: PropTypes.string,
    status: PropTypes.number,
    name: PropTypes.string,
};

function DocPage(props) {
    const { children, description, status, name } = props;

    return (
        <div role="main" {...createTestHook(__filename, name)}>
            <a id="navSkip">
                {name && <Heading level={1}>{name}</Heading>}
            </a>
            <Status level={status} />
            <Markdown text={description} />
            {children}
        </div>
    );
}

export default DocPage;
DocPage.propTypes = propTypes;
