import React, { PropTypes } from 'react';
import css from './Status.css';

const strings = {
    0: 'Deprecated',
    1: 'Alpha',
    2: 'Beta',
    3: 'Stable',
    4: 'Locked',
};
const propTypes = {
    level: PropTypes.number,
};

function Status(props) {
    const { level } = props;
    return level != null && (
        <div>
            <div className={css[`level${level}`]}>
                API Status: {strings[level]}
            </div>
        </div>
    );
}

Status.propTypes = propTypes;
export default Status;
