import React, { PropTypes } from 'react';
import Clickable from 'splunk-ui/components/Clickable';
import css from './Nav.css';

const propTypes = {
    children: PropTypes.string,
    to: PropTypes.string.isRequired,
    selected: PropTypes.bool,
};

function Item(props) {
    const { children, selected, ...other } = props;

    return (
        <li className={css.li} data-selected={selected || null}>
            <Clickable className={css.link} {...other} disabled={selected}>{children}</Clickable>
        </li>
    );
}

Item.propTypes = propTypes;
export default Item;
