import React from 'react';
import DocExample from 'components/DocExample';
import DocPage from 'components/DocPage';
import Specs from 'components/Specs';
import packageData from 'splunk-ui/components/Accordion/package.json';
import accordionDocs from '!!docgen-loader!splunk-ui/components/Accordion';
import panelDocs from '!!docgen-loader!splunk-ui/components/Accordion/Panel';

const examples = require.context('splunk-ui/components/Accordion/examples/');
const examplesRaw = require.context('!!raw!splunk-ui/components/Accordion/examples/');

function Accordion() {
    return (
        <DocPage {...packageData} description={accordionDocs[0].description}>
            <DocExample
                title="Controlled"
                description="In this example, the open panel is controlled by the parent component."
                code={examplesRaw('./Controlled')}
                example={examples('./Controlled').default}
            />
            <DocExample
                title="Uncontrolled"
                description="In this example, the `Accordion` is used as an uncontrolled component."
                code={examplesRaw('./Uncontrolled')}
                example={examples('./Uncontrolled').default}
            />
            <DocExample
                title="An Accordion in a Dropdown"
                code={examplesRaw('./InDropdown')}
                example={examples('./InDropdown').default}
            />
            <Specs title="Accordion" docs={accordionDocs[0]} />
            <Specs title="Accordion.Panel" docs={panelDocs[0]} />
        </DocPage>
    );
}

export default Accordion;
