import React from 'react';
import DocExample from 'components/DocExample';
import DocPage from 'components/DocPage';
import packageData from 'splunk-ui/util/filter/package.json';

const examples = require.context('splunk-ui/util/filter/examples/', false, /^\.\/.*/);
const examplesRaw = require.context('!!raw-loader!splunk-ui/util/filter/examples/',
                        false, /^\.\/.*/);

function AutoComplete() {
    return (
        <DocPage {...packageData} description={'Filter utility example'}>
            <DocExample
                title="Basic"
                description="Basic example."
                code={examplesRaw('./Basic')}
                example={examples('./Basic').default}
            />
        </DocPage>
    );
}

export default AutoComplete;
