import React from 'react';
import DocExample from 'components/DocExample';
import DocPage from 'components/DocPage';
import Specs from 'components/Specs';
import packageData from 'splunk-ui/components/Button/package.json';
import docs from '!!docgen-loader!splunk-ui/components/Button';

const examples = require.context('splunk-ui/components/Button/examples/');
const examplesRaw = require.context('!!raw!splunk-ui/components/Button/examples/');

function Buttons() {
    return (
        <DocPage {...packageData} description={docs[0].description}>
            <DocExample
                title="Basic"
                description="Basic button appearances"
                code={examplesRaw('./Basic')}
                example={examples('./Basic').default}
            />
            <DocExample
                title="Disabled"
                code={examplesRaw('./Disabled')}
                example={examples('./Disabled').default}
            />
            <DocExample
                title="Error"
                description={`Buttons can have an error state. Consider including some
                    other indication of the status, such as in icon or error message, as this
                    color differentiation is insuffient for accessibility.`}
                code={examplesRaw('./Error')}
                example={examples('./Error').default}
            />
            <DocExample
                title="Icons"
                code={examplesRaw('./Icons')}
                example={examples('./Icons').default}
            />
            <DocExample
                title="Menus"
                code={examplesRaw('./Menus')}
                example={examples('./Menus').default}
            />
            <DocExample
                title="Size"
                description="Small and Large Buttons are also supported."
                code={examplesRaw('./Size')}
                example={examples('./Size').default}
            />
            <DocExample
                title="Selected"
                code={examplesRaw('./Selected')}
                example={examples('./Selected').default}
            />
            <DocExample
                title="To"
                description="Adding a 'to' attribute to make the button behave like a link."
                code={examplesRaw('./To')}
                example={examples('./To').default}
            />
            <DocExample
                title="Block Button"
                code={examplesRaw('./Block')}
                example={examples('./Block').default}
            />
            <DocExample
                title="Truncated"
                code={examplesRaw('./Truncated')}
                example={examples('./Truncated').default}
            />
            <Specs docs={docs[0]} />
        </DocPage>
    );
}

export default Buttons;
