import React from 'react';
import DocExample from 'components/DocExample';
import DocPage from 'components/DocPage';
import Specs from 'components/Specs';
import packageData from 'splunk-ui/components/Calendar/package.json';
import docs from '!!docgen-loader!splunk-ui/components/Calendar';

const examples = require.context('splunk-ui/components/Calendar/examples/');
const examplesRaw = require.context('!!raw!splunk-ui/components/Calendar/examples/');

function Calendar() {
    return (
        <DocPage {...packageData} description={docs[0].description}>
            <DocExample
                title="Basic"
                code={examplesRaw('./Basic')}
                example={examples('./Basic').default}
            />
            <Specs docs={docs[0]} />
        </DocPage>
    );
}

export default Calendar;
