import React from 'react';
import DocExample from 'components/DocExample';
import DocPage from 'components/DocPage';
import Specs from 'components/Specs';
import docs from '!!docgen-loader!splunk-ui/components/Code';
import packageData from 'splunk-ui/components/Code/package.json';

const examples = require.context('splunk-ui/components/Code/examples/');
const examplesRaw = require.context('!!raw!splunk-ui/components/Code/examples/');

function Code() {
    return (
        <DocPage {...packageData} description={docs[0].description}>
            <DocExample
                title="Default"
                description="The default usage"
                code={examplesRaw('./Default')}
                example={examples('./Default').default}
            />
            <DocExample
                title="No indent guideline"
                description="In this example no indent guideline is drawn."
                code={examplesRaw('./HideIndent')}
                example={examples('./HideIndent').default}
            />
            <DocExample
                title="Language"
                description="Specify the language by setting the prop"
                code={examplesRaw('./Language')}
                example={examples('./Language').default}
            />
            <Specs docs={docs[0]} />
        </DocPage>
    );
}

export default Code;
