import React from 'react';
import DocExample from 'components/DocExample';
import DocPage from 'components/DocPage';
import Specs from 'components/Specs';
import packageData from 'splunk-ui/components/CollapsiblePanel/package.json';
import docs from '!!docgen-loader!splunk-ui/components/CollapsiblePanel';

const examples = require.context('splunk-ui/components/CollapsiblePanel/examples/');
const examplesRaw = require.context('!!raw!splunk-ui/components/CollapsiblePanel/examples/');

function CollapsiblePanel() {
    return (
        <DocPage {...packageData} description={docs[0].description}>
            <DocExample
                title="Basic Uncontrolled"
                code={examplesRaw('./BasicUncontrolled')}
                example={examples('./BasicUncontrolled').default}
            />
            <DocExample
                title="Basic Controlled"
                code={examplesRaw('./BasicControlled')}
                example={examples('./BasicControlled').default}
            />
            <DocExample
                title="Multi Uncontrolled"
                code={examplesRaw('./MultiUncontrolled')}
                example={examples('./MultiUncontrolled').default}
            />
            <DocExample
                title="Multi Controlled"
                code={examplesRaw('./MultiControlled')}
                example={examples('./MultiControlled').default}
            />
            <Specs docs={docs[0]} />
        </DocPage>
    );
}

export default CollapsiblePanel;
