import React from 'react';
import DocPage from 'components/DocPage';
import DocExample from 'components/DocExample';
import Specs from 'components/Specs';
import packageData from 'splunk-ui/components/Color/package.json';
import docs from '!!docgen-loader!splunk-ui/components/Color';

const examples = require.context('splunk-ui/components/Color/examples/');
const examplesRaw = require.context('!!raw!splunk-ui/components/Color/examples/');

function Color() {
    return (
        <DocPage {...packageData} description={docs[0].description}>
            <DocExample
                title="Uncontrolled"
                description="In this example, the `Color` is used as an uncontrolled component."
                code={examplesRaw('./Uncontrolled')}
                example={examples('./Uncontrolled').default}
            />
            <DocExample
                title="Controlled"
                description="In this example, the `Color` is used as a controlled component."
                code={examplesRaw('./Controlled')}
                example={examples('./Controlled').default}
            />
            <Specs docs={docs[0]} />
        </DocPage>
    );
}

export default Color;
