import React from 'react';
import DocExample from 'components/DocExample';
import DocPage from 'components/DocPage';
import Specs from 'components/Specs';
import packageData from 'splunk-ui/components/Concertina/package.json';
import docs from '!!docgen-loader!splunk-ui/components/Concertina';
import docsPanel from '!!docgen-loader!splunk-ui/components/Concertina/Panel';

const examples = require.context('splunk-ui/components/Concertina/examples/');
const examplesRaw = require.context('!!raw!splunk-ui/components/Concertina/examples/');

function Concertina() {
    return (
        <DocPage {...packageData} description={docs[0].description}>
            <DocExample
                title="Example"
                code={examplesRaw('./Basic')}
                example={examples('./Basic').default}
            />
            <DocExample
                title="Status and Description"
                description={`Since Concertina is intended to be used for configuration forms,
                    the ability to show which panel is in error can be critical to usability.`}
                code={examplesRaw('./Status')}
                example={examples('./Status').default}
            />
            <Specs docs={docs[0]} title="Concertina" />
            <Specs docs={docsPanel[0]} title="Concertina.Panel" />
        </DocPage>
    );
}

export default Concertina;
