import React from 'react';
import DocExample from 'components/DocExample';
import DocPage from 'components/DocPage';
import Specs from 'components/Specs';
import packageData from 'splunk-ui/components/Date/package.json';
import docs from '!!docgen-loader!splunk-ui/components/Date';

const examples = require.context('splunk-ui/components/Date/examples/');
const examplesRaw = require.context('!!raw!splunk-ui/components/Date/examples/');

function Date() {
    return (
        <DocPage {...packageData} description={docs[0].description}>
            <DocExample
                title="Basic"
                description="Basic example."
                code={examplesRaw('./Basic')}
                example={examples('./Basic').default}
            />
            <DocExample
                title="CustomDate"
                description="Example of setting the initial date and format."
                code={examplesRaw('./CustomDate')}
                example={examples('./CustomDate').default}
            />
            <DocExample
                title="Disabled"
                description="Example of a disabled Date."
                code={examplesRaw('./Disabled')}
                example={examples('./Disabled').default}
            />
            <DocExample
                title="Controlled"
                description="Example of a controlled Date."
                code={examplesRaw('./Controlled')}
                example={examples('./Controlled').default}
            />
            <Specs docs={docs[0]} />
        </DocPage>
    );
}

export default Date;
