import React from 'react';
import DocExample from 'components/DocExample';
import DocPage from 'components/DocPage';
import Specs from 'components/Specs';
import packageData from 'splunk-ui/components/DefinitionList/package.json';
import docs from '!!docgen-loader!splunk-ui/components/DefinitionList';

const examples = require.context('splunk-ui/components/DefinitionList/examples/');
const examplesRaw = require.context('!!raw!splunk-ui/components/DefinitionList/examples/');

function DefinitionList() {
    return (
        <DocPage {...packageData} description={docs[0].description}>
            <DocExample
                title="Basic"
                description="Basic example."
                code={examplesRaw('./Basic')}
                example={examples('./Basic').default}
            />
            <DocExample
                title="Customize"
                description="Example with custom width."
                code={examplesRaw('./Customize')}
                example={examples('./Customize').default}
            />
            <Specs docs={docs[0]} />
        </DocPage>
    );
}

export default DefinitionList;
