import React from 'react';
import DocExample from 'components/DocExample';
import DocPage from 'components/DocPage';
import Specs from 'components/Specs';
import packageData from 'splunk-ui/components/Dropdown/package.json';
import docs from '!!docgen-loader!splunk-ui/components/Dropdown';

const examples = require.context('splunk-ui/components/Dropdown/examples/');
const examplesRaw = require.context('!!raw!splunk-ui/components/Dropdown/examples/');

function Dropdown() {
    return (
        <DocPage {...packageData} description={docs[0].description}>
            <DocExample
                title="Basic Dropdown"
                description="The dropdown can contain any content.
                    However, the content must constrain it's own width."
                code={examplesRaw('./Basic')}
                example={examples('./Basic').default}
            />
            <DocExample
                title="Dropdown Menu"
                code={examplesRaw('./BasicMenu')}
                example={examples('./BasicMenu').default}
            />
            <DocExample
                title="Other Toggles"
                description="A Button or Link, or any other component with an onClick event
                    can be provided as a toggle."
                code={examplesRaw('./OtherToggles')}
                example={examples('./OtherToggles').default}
            />
            <DocExample
                title="Controlled Dropdown"
                code={examplesRaw('./ControlledDropdown')}
                example={examples('./ControlledDropdown').default}
            />
            <Specs docs={docs[0]} />
        </DocPage>
    );
}
export default Dropdown;
