import React from 'react';
import DocPage from 'components/DocPage';
import DocExample from 'components/DocExample';
import Specs from 'components/Specs';
import packageData from 'splunk-ui/components/File/package.json';
import docs from '!!docgen-loader!splunk-ui/components/File';
import docsItem from '!!docgen-loader!splunk-ui/components/File/Item';

const examples = require.context('splunk-ui/components/File/examples/');
const examplesRaw = require.context('!!raw!splunk-ui/components/File/examples/');

function File() {
    return (
        <DocPage {...packageData} description={docs[0].description}>
            <DocExample
                title="Single File"
                description={`Here, selecting a new file will replace the existing file.
                    \`small\` is intended for use inside a ControlGroup.
                    \`large\` will take all files dropped on the page, so it's not possible to
                    display more than one at a time.` }
                code={examplesRaw('./Single')}
                example={examples('./Single').default}
            />
            <DocExample
                title="Multifile"
                description="By storing an array of items, multiple files can be supported"
                code={examplesRaw('./Multi')}
                example={examples('./Multi').default}
            />
            <DocExample
                title="Custom Children"
                description={`It's not necessary to use File.Item as a child. File will still return
                    click events on children as a removal request.`}
                code={examplesRaw('./CustomChildren')}
                example={examples('./CustomChildren').default}
            />
            <DocExample
                title="Progress"
                description="The file item can display it's upload progress."
                code={examplesRaw('./Progress')}
                example={examples('./Progress').default}
            />
            <DocExample
                title="Large"
                description={`This example must open in a modal as the \`large\` size can be the
                    only File on the page. Dropping a file anywhere will upload the file.`}
                code={examplesRaw('./Large')}
                example={examples('./Large').default}
            />
            <DocExample
                title="Error"
                description={`The File and File.Item have separate error states. Generally, they
                    should not be used together. Either the File is in error (e.g. required field),
                    or an Item is in error (e.g. incorrect file type)`}
                code={examplesRaw('./Error')}
                example={examples('./Error').default}
            />
            <Specs docs={docs[0]} title="File" />
            <Specs docs={docsItem[0]} title="File.Item" />
        </DocPage>
    );
}

export default File;
