import React from 'react';
import DocExample from 'components/DocExample';
import DocPage from 'components/DocPage';
import Specs from 'components/Specs';
import packageData from 'splunk-ui/components/Heading/package.json';
import docs from '!!docgen-loader!splunk-ui/components/Heading';

const examples = require.context('splunk-ui/components/Heading/examples/');
const examplesRaw = require.context('!!raw!splunk-ui/components/Heading/examples/');

function Heading() {
    return (
        <DocPage {...packageData} description={docs[0].description}>
            <DocExample
                title="Examples"
                code={examplesRaw('./Basic')}
                example={examples('./Basic').default}
            />

            <Specs docs={docs[0]} />
        </DocPage>
    );
}

export default Heading;
