import React from 'react';
import DocExample from 'components/DocExample';
import DocPage from 'components/DocPage';
import Specs from 'components/Specs';
import docs from '!!docgen-loader!splunk-ui/icons/SVG';

const examples = require.context('splunk-ui/icons/examples/');
const examplesRaw = require.context('!!raw!splunk-ui/icons/examples/');

function Icon() {
    return (
        <DocPage name="Icons" status={1} description={docs[0].description}>
            <DocExample
                title="Basic Example"
                code={examplesRaw('./Basic')}
                example={examples('./Basic').default}
            >
                By default, icons are 75% the font size and the same color. This makes them appear
                to roughly align with the text. By default, icons are aligned in the middle
                vertically. Set style={'{{ verticalAlign: baseline }} '}
                to align the bottom of the Icon and the bottom of the capital letters.
            </DocExample>
            <DocExample
                title="Size"
                code={examplesRaw('./Size')}
                example={examples('./Size').default}
            >
                Size, height or width props can be set in ems by providing a number. A unit must be
                provided when the values are strings. A pink background has been added to show
                the outer dimensions of the icon.
            </DocExample>
            <DocExample
                title="Color"
                code={examplesRaw('./Color')}
                example={examples('./Color').default}
            >
                Color is always inherited from the parent.
            </DocExample>
            <DocExample
                title="All Icons"
                example={examples('./All').default}
            />

            <Specs docs={docs[0]} />
        </DocPage>
    );
}

export default Icon;
