import React from 'react';
import DocPage from 'components/DocPage';
import DocExample from 'components/DocExample';
import { _ } from 'splunk-ui/util/i18n';
import packageData from 'splunk-ui/util/i18n/package.json';

const examples = require.context('splunk-ui/util/i18n/examples/');
const examplesRaw = require.context('!!raw!splunk-ui/util/i18n/examples/');

function Internationalization() {
    return (
        <DocPage {...packageData}>
            <DocExample
                title={_('Translating Splunk Apps')}
                code={examplesRaw('./Basic')}
                example={examples('./Basic').default}
            />
        </DocPage>
    );
}

export default Internationalization;
