import React from 'react';
import DocExample from 'components/DocExample';
import DocPage from 'components/DocPage';
import Specs from 'components/Specs';
import docs from '!!docgen-loader!splunk-ui/components/JSONTree';
import packageData from 'splunk-ui/components/JSONTree/package.json';

const examples = require.context('splunk-ui/components/JSONTree/examples/');
const examplesRaw = require.context('!!raw-loader!splunk-ui/components/JSONTree/examples/');

function JSONTree() {
    return (
        <DocPage {...packageData} description={docs[0].description}>
            <DocExample
                title="Basic"
                description="Basic example of JSONTree view."
                code={examplesRaw('./Basic')}
                example={examples('./Basic').default}
            />
            <DocExample
                title="With Events"
                description="Click the value to see the interaction"
                code={examplesRaw('./Events')}
                example={examples('./Events').default}
            />
            <Specs docs={docs[0]} />
        </DocPage>
    );
}

export default JSONTree;
