import React from 'react';
import DocExample from 'components/DocExample';
import DocPage from 'components/DocPage';
import Specs from 'components/Specs';
import packageData from 'splunk-ui/components/Link/package.json';
import docs from '!!docgen-loader!splunk-ui/components/Link';

const examples = require.context('splunk-ui/components/Link/examples/');
const examplesRaw = require.context('!!raw!splunk-ui/components/Link/examples/');

function Link() {
    return (
        <DocPage {...packageData} description={docs[0].description}>
            <DocExample
                title="Example"
                description="Use the Link component instead of an <a> tag."
                code={examplesRaw('./Basic')}
                example={examples('./Basic').default}
            />
            <DocExample
                title="New Tab"
                description="Setting target to openInNewContext to true will add an icon and set
                    target to _blank."
                code={examplesRaw('./NewTab')}
                example={examples('./NewTab').default}
            />
            <DocExample
                title="Disabled"
                description="Links can be disabled. The href will be removed from the markup."
                code={examplesRaw('./Disabled')}
                example={examples('./Disabled').default}
            />
            <DocExample
                title="In a Paragraph"
                code={examplesRaw('./Inline')}
                example={examples('./Inline').default}
            />

            <Specs docs={docs[0]} />
        </DocPage>
    );
}

export default Link;
