import React from 'react';
import DocExample from 'components/DocExample';
import DocPage from 'components/DocPage';
import Specs from 'components/Specs';
import packageData from 'splunk-ui/components/List/package.json';
import docs from '!!docgen-loader!splunk-ui/components/List';

const examples = require.context('splunk-ui/components/List/examples/');
const examplesRaw = require.context('!!raw!splunk-ui/components/List/examples/');

function List() {
    return (
        <DocPage {...packageData} description={docs[0].description}>
            <DocExample
                title="Disc"
                description="Disc (unordered) example."
                code={examplesRaw('./Disc')}
                example={examples('./Disc').default}
            />
            <DocExample
                title="Decimal"
                description="Decimal (ordered) example."
                code={examplesRaw('./Decimal')}
                example={examples('./Decimal').default}
            />
            <DocExample
                title="Lower Alpha"
                description="Lower alpha (ordered) example."
                code={examplesRaw('./LowerAlpha')}
                example={examples('./LowerAlpha').default}
            />
            <DocExample
                title="Upper Alpha"
                description="Upper alpha (ordered) example."
                code={examplesRaw('./UpperAlpha')}
                example={examples('./UpperAlpha').default}
            />
            <Specs docs={docs[0]} />
        </DocPage>
    );
}

export default List;
