import React from 'react';
import DocExample from 'components/DocExample';
import DocPage from 'components/DocPage';
import Specs from 'components/Specs';
import packageData from 'splunk-ui/components/Logo/package.json';
import docs from '!!docgen-loader!splunk-ui/components/Logo';

const examples = require.context('splunk-ui/components/Logo/examples/');
const examplesRaw = require.context('!!raw!splunk-ui/components/Logo/examples/');

function Logo() {
    return (
        <DocPage {...packageData} description={docs[0].description}>
            <DocExample
                title="Light"
                description="Examples on Light Background."
                code={examplesRaw('./Basic')}
                example={examples('./Basic').default}
            />
            <DocExample
                title="Dark"
                description="Examples on a Dark Background."
                code={examplesRaw('./Dark')}
                example={examples('./Dark').default}
            />
            <Specs docs={docs[0]} />
        </DocPage>
    );
}

export default Logo;
