import React from 'react';
import DocExample from 'components/DocExample';
import DocPage from 'components/DocPage';
import Specs from 'components/Specs';
import docs from '!!docgen-loader!splunk-ui/components/Markdown';
import packageData from 'splunk-ui/components/Markdown/package.json';

const examples = require.context('splunk-ui/components/Markdown/examples/', false, /jsx$/);
const examplesRaw = require.context('!!raw!splunk-ui/components/Markdown/examples/');


function Markdown() {
    return (
        <DocPage {...packageData} description={docs[0].description}>
            <DocExample
                title="Basic"
                description="Basic example."
                code={examplesRaw('./Basic')}
                example={examples('./Basic.jsx').default}
            />
            <DocExample
                title="I18n"
                description="Combine with gettext interpolation"
                code={examplesRaw('./I18n')}
                example={examples('./I18n.jsx').default}
            />
            <Specs docs={docs[0]} />
        </DocPage>
    );
}

export default Markdown;
