import React from 'react';
import DocExample from 'components/DocExample';
import DocPage from 'components/DocPage';
import Specs from 'components/Specs';
import packageData from 'splunk-ui/components/Menu/package.json';
import docs from '!!docgen-loader!splunk-ui/components/Menu';
import docsMenu from '!!docgen-loader!splunk-ui/components/Menu/Item';

const examples = require.context('splunk-ui/components/Menu/examples/');
const examplesRaw = require.context('!!raw!splunk-ui/components/Menu/examples/');

function Menu() {
    return (
        <DocPage {...packageData} description={docs[0].description}>
            <DocExample
                title="Basic"
                description={`Basic example. Since, this Menu is not in a Popover, retainFocus
                    is disabled.`}
                code={examplesRaw('./Basic')}
                example={examples('./Basic').default}
            />
            <DocExample
                title="Icons"
                code={examplesRaw('./Icons')}
                example={examples('./Icons').default}
            />
            <DocExample
                title="Links"
                description="Menu items can contain links by including a 'to' attribute."
                code={examplesRaw('./Links')}
                example={examples('./Links').default}
            />
            <DocExample
                title="Descriptions"
                description="Descriptions can be placed below or to the right of the label."
                code={examplesRaw('./Descriptions')}
                example={examples('./Descriptions').default}
            />
            <DocExample
                title="Selectable and Selected"
                description="Items can be selected or selectable, which ensures space is
                    reserved for the checkmark."
                code={examplesRaw('./Selectable')}
                example={examples('./Selectable').default}
            />
            <DocExample
                title="Complex"
                description="All the options can be used together."
                code={examplesRaw('./Complex')}
                example={examples('./Complex').default}
            />
            <DocExample
                title="Truncation"
                description={`Labels that are too long can be optionally constrained to one
                    line and trunctated.`}
                code={examplesRaw('./Truncate')}
                example={examples('./Truncate').default}
            />
            <DocExample
                title="Disabled"
                code={examplesRaw('./Disabled')}
                example={examples('./Disabled').default}
            />

            <Specs docs={docs[0]} title="Menu" />
            <Specs docs={docsMenu[0]} title="Menu.Item" />
        </DocPage>
    );
}

export default Menu;
