import React from 'react';
import DocExample from 'components/DocExample';
import DocPage from 'components/DocPage';
import Specs from 'components/Specs';
import packageData from 'splunk-ui/components/Message/package.json';
import docs from '!!docgen-loader!splunk-ui/components/Message';

const examples = require.context('splunk-ui/components/Message/examples/');
const examplesRaw = require.context('!!raw!splunk-ui/components/Message/examples/');

function Message() {
    return (
        <DocPage {...packageData} description={docs[0].description}>
            <DocExample
                title="Basic"
                description="Basic example."
                code={examplesRaw('./Basic')}
                example={examples('./Basic').default}
            />
            <DocExample
                title="Content"
                description="Example with custom children."
                code={examplesRaw('./Content')}
                example={examples('./Content').default}
            />
            <DocExample
                title="Wrap"
                description="Example with wrapping text."
                code={examplesRaw('./Wrap')}
                example={examples('./Wrap').default}
            />
            <Specs docs={docs[0]} />
        </DocPage>
    );
}

export default Message;
