import React from 'react';
import DocExample from 'components/DocExample';
import DocPage from 'components/DocPage';
import Specs from 'components/Specs';
import packageData from 'splunk-ui/components/Modal/package.json';
import docs from '!!docgen-loader!splunk-ui/components/Modal';
import docsBody from '!!docgen-loader!splunk-ui/components/Modal/Body';
import docsFooter from '!!docgen-loader!splunk-ui/components/Modal/Footer';
import docsHeader from '!!docgen-loader!splunk-ui/components/Modal/Header';

const examples = require.context('splunk-ui/components/Modal/examples/');
const examplesRaw = require.context('!!raw!splunk-ui/components/Modal/examples/');

function Modal() {
    return (
        <DocPage {...packageData} description={docs[0].description}>
            <DocExample
                title="Basic Modal Container"
                description="This example is just a simple container without standard elements."
                code={examplesRaw('./Basic')}
                example={examples('./Basic').default}
            />
            <DocExample
                title="Typical Usage"
                description={`Basic Splunk styled modal. By defaullt, the Modal will take on the
                    width of the content, so be sure that width is set on the children. Alternately,
                    set with on the Modal or Modal.Body with an inline style.`}
                code={examplesRaw('./SimpleSplunk')}
                example={examples('./SimpleSplunk').default}
            />
            <DocExample
                title="Undismissable"
                description={`To create a modal that cannot be dismissed, simply
                     omit the 'onClose' callback.`}
                code={examplesRaw('./Undismissable')}
                example={examples('./Undismissable').default}
            />

            <Specs docs={docs[0]} title="Modal" />
            <Specs docs={docsHeader[0]} title="Modal.Header" />
            <Specs docs={docsBody[0]} title="Modal.Body" />
            <Specs docs={docsFooter[0]} title="Modal.Footer" />
        </DocPage>
    );
}

export default Modal;
