import React from 'react';
import DocExample from 'components/DocExample';
import DocPage from 'components/DocPage';
import Specs from 'components/Specs';
import docs from '!!docgen-loader!splunk-ui/components/Number';
import packageData from 'splunk-ui/components/Number/package.json';

const examples = require.context('splunk-ui/components/Number/examples/');
const examplesRaw = require.context('!!raw!splunk-ui/components/Number/examples/');

function Number() {
    return (
        <DocPage {...packageData} description={docs[0].description}>
            <DocExample
                title="Basic"
                description={`Number requires a value prop and an onChange
                    callback to update the value prop for most use cases. Set value to null
                    `}
                code={examplesRaw('./Basic')}
                example={examples('./Basic').default}
            />
            <DocExample
                title="Uncontrolled"
                description={`Alternately, Number can be uncontrolled and optionally provided a
                    defaultValue. The onChange callback still fires. The value prop cannot be
                    set or updated externally.`}
                code={examplesRaw('./Uncontrolled')}
                example={examples('./Uncontrolled').default}
            />
            <DocExample
                title="Limits"
                description={'Number accepts a min, max, step, and number of decimals.'}
                code={examplesRaw('./Limits')}
                example={examples('./Limits').default}
            />
            <Specs docs={docs[0]} />
        </DocPage>
    );
}

export default Number;
