import React from 'react';
import DocExample from 'components/DocExample';
import DocPage from 'components/DocPage';
import Specs from 'components/Specs';
import packageData from 'splunk-ui/components/Popover/package.json';
import docs from '!!docgen-loader!splunk-ui/components/Popover';

const examples = require.context('splunk-ui/components/Popover/examples/');
const examplesRaw = require.context('!!raw!splunk-ui/components/Popover/examples/');

function Popovers() {
    return (
        <DocPage {...packageData} description={docs[0].description}>
            <DocExample
                title="Example"
                description="Click the button to view the popover."
                code={examplesRaw('./Basic')}
                example={examples('./Basic').default}
            />
            <Specs docs={docs[0]} />
        </DocPage>
    );
}

export default Popovers;
