import React from 'react';
import DocExample from 'components/DocExample';
import DocPage from 'components/DocPage';
import Specs from 'components/Specs';
import packageData from 'splunk-ui/components/Progress/package.json';
import docs from '!!docgen-loader!splunk-ui/components/Progress';

const examples = require.context('splunk-ui/components/Progress/examples/');
const examplesRaw = require.context('!!raw!splunk-ui/components/Progress/examples/');

function Progress() {
    return (
        <DocPage {...packageData} description={docs[0].description}>
            <DocExample
                title="Example"
                code={examplesRaw('./Basic')}
                example={examples('./Basic').default}
            />
            <Specs docs={docs[0]} />
        </DocPage>
    );
}

export default Progress;
