import React from 'react';
import DocExample from 'components/DocExample';
import DocPage from 'components/DocPage';
import Specs from 'components/Specs';
import packageData from 'splunk-ui/components/RadioBar/package.json';
import docs from '!!docgen-loader!splunk-ui/components/RadioBar';
import docsOption from '!!docgen-loader!splunk-ui/components/RadioBar/Option';

const examples = require.context('splunk-ui/components/RadioBar/examples/');
const examplesRaw = require.context('!!raw!splunk-ui/components/RadioBar/examples/');

function RadioBar() {
    return (
        <DocPage {...packageData} description={docs[0].description}>
            <DocExample
                title="Basic"
                description="Basic example."
                code={examplesRaw('./Basic')}
                example={examples('./Basic').default}
            />
            <DocExample
                title="Uncontrolled"
                description="Uncontrolled example."
                code={examplesRaw('./Uncontrolled')}
                example={examples('./Uncontrolled').default}
            />
            <Specs docs={docs[0]} title="RadioBar" />
            <Specs docs={docsOption[0]} title="RadioBar.Option" />
        </DocPage>
    );
}

export default RadioBar;
