import React from 'react';
import DocExample from 'components/DocExample';
import DocPage from 'components/DocPage';
import Specs from 'components/Specs';
import packageData from 'splunk-ui/components/RadioList/package.json';
import docs from '!!docgen-loader!splunk-ui/components/RadioList';
import docsOption from '!!docgen-loader!splunk-ui/components/RadioList/Option';

const examples = require.context('splunk-ui/components/RadioList/examples/');
const examplesRaw = require.context('!!raw!splunk-ui/components/RadioList/examples/');

function Radio() {
    return (
        <DocPage {...packageData}>
            <DocExample
                title="Basic"
                description={`RadioList requires a value prop and an onChange
                    callback to update the value prop for most use cases.`}
                code={examplesRaw('./Basic')}
                example={examples('./Basic').default}
            />
            <DocExample
                title="Uncontrolled"
                description={`Alternately, RadioList can be uncontrolled and optionally provided a
                    defaultValue. The onChange callback still fires. The value prop cannot be
                    set or updated externally.`}
                code={examplesRaw('./Uncontrolled')}
                example={examples('./Uncontrolled').default}
            />
            <DocExample
                title="Composing"
                description="Additional children are permitted."
                code={examplesRaw('./Composing')}
                example={examples('./Composing').default}
            />
            <Specs docs={docs[0]} title="RadioList" />
            <Specs docs={docsOption[0]} title="RadioList.Option" />
        </DocPage>
    );
}

export default Radio;
