import React from 'react';
import DocExample from 'components/DocExample';
import DocPage from 'components/DocPage';
import Specs from 'components/Specs';
import packageData from 'splunk-ui/components/ScreenReaderContent/package.json';
import docs from '!!docgen-loader!splunk-ui/components/ScreenReaderContent';

const examples = require.context('splunk-ui/components/ScreenReaderContent/examples/');
const examplesRaw = require.context('!!raw!splunk-ui/components/ScreenReaderContent/examples/');

function ScreenReaderContent() {
    return (
        <DocPage {...packageData} description={docs[0].description}>
            <DocExample
                title="Example"
                description="Click 'Show Code' to view the hidden screen reader content."
                code={examplesRaw('./Basic')}
                example={examples('./Basic').default}
            />

            <Specs docs={docs[0]} />
        </DocPage>
    );
}

export default ScreenReaderContent;
