import React from 'react';
import DocExample from 'components/DocExample';
import DocPage from 'components/DocPage';
import Specs from 'components/Specs';
import packageData from 'splunk-ui/components/Scroll/package.json';
import docs from '!!docgen-loader!splunk-ui/components/Scroll';

const examples = require.context('splunk-ui/components/Scroll/examples/');
const examplesRaw = require.context('!!raw!splunk-ui/components/Scroll/examples/');

function Scroll() {
    return (
        <DocPage {...packageData} description={docs[0].description}>
            <DocExample
                title="Controlled"
                description="The controlled method must be used to set the scrollPosition."
                code={examplesRaw('./Controlled')}
                example={examples('./Controlled').default}
            />
            <DocExample
                title="Uncontrolled without Scroll Propagation"
                description="The uncontrolled approach is only useful stopping propagation. Here,
                scrolling with the mouseWheel over the container will not scroll the window."
                code={examplesRaw('./Uncontrolled')}
                example={examples('./Uncontrolled').default}
            />
            <Specs docs={docs[0]} />
        </DocPage>
    );
}

export default Scroll;
