import React from 'react';
import DocPage from 'components/DocPage';
import DocExample from 'components/DocExample';
import Specs from 'components/Specs';
import packageData from 'splunk-ui/components/Slider/package.json';
import docs from '!!docgen-loader!splunk-ui/components/Slider';

const examples = require.context('splunk-ui/components/Slider/examples/');
const examplesRaw = require.context('!!raw!splunk-ui/components/Slider/examples/');

function Slider() {
    return (
        <DocPage {...packageData} description={docs[0].description}>
            <DocExample
                title="Basic"
                description="Basic example."
                code={examplesRaw('./Basic')}
                example={examples('./Basic').default}
            />
            <DocExample
                title="Controlled"
                description="Slider controlling a Text input."
                code={examplesRaw('./Controlled')}
                example={examples('./Controlled').default}
            />
            <Specs docs={docs[0]} />
        </DocPage>
    );
}

export default Slider;
