import React from 'react';
import DocPage from 'components/DocPage';
import DocExample from 'components/DocExample';
import Specs from 'components/Specs';
import packageData from 'splunk-ui/components/SlidingPanels/package.json';
import docs from '!!docgen-loader!splunk-ui/components/SlidingPanels';

const examples = require.context('splunk-ui/components/SlidingPanels/examples/');
const examplesRaw = require.context('!!raw!splunk-ui/components/SlidingPanels/examples/');

function SlidingPanels() {
    return (
        <DocPage {...packageData} description={docs[0].description}>
            <DocExample
                title="Basic"
                code={examplesRaw('./Basic')}
                example={examples('./Basic').default}
            />
            <DocExample
                title="In a Dropdown"
                code={examplesRaw('./Dropdown')}
                example={examples('./Dropdown').default}
            />
            <DocExample
                title="In a Modal"
                code={examplesRaw('./Modal')}
                example={examples('./Modal').default}
            />
            <Specs docs={docs[0]} />
        </DocPage>
    );
}

export default SlidingPanels;
