import React from 'react';
import DocExample from 'components/DocExample';
import DocPage from 'components/DocPage';
import Specs from 'components/Specs';
import packageData from 'splunk-ui/components/StaticContent/package.json';
import docs from '!!docgen-loader!splunk-ui/components/StaticContent';

const examples = require.context('splunk-ui/components/StaticContent/examples/');
const examplesRaw = require.context('!!raw!splunk-ui/components/StaticContent/examples/');

function StaticContent() {
    return (
        <DocPage {...packageData} description={docs[0].description}>
            <DocExample
                title="Basic"
                description="Basic example, renders static content within a ControlGroup."
                code={examplesRaw('./Basic')}
                example={examples('./Basic').default}
            />
            <DocExample
                title="FieldValue"
                description="StaticContent with other components in a ControlGroup"
                code={examplesRaw('./FieldValue')}
                example={examples('./FieldValue').default}
            />
            <Specs docs={docs[0]} />
        </DocPage>
    );
}

export default StaticContent;
