import React from 'react';
import DocExample from 'components/DocExample';
import DocPage from 'components/DocPage';
import Specs from 'components/Specs';
import packageData from 'splunk-ui/components/StepBar/package.json';
import docs from '!!docgen-loader!splunk-ui/components/StepBar';
import docsPane from '!!docgen-loader!splunk-ui/components/StepBar/Step';

const examples = require.context('splunk-ui/components/StepBar/examples/');
const examplesRaw = require.context('!!raw!splunk-ui/components/StepBar/examples/');

function StepBar() {
    return (
        <DocPage {...packageData} description={docs[0].description}>
            <DocExample
                title="Progress"
                code={examplesRaw('./Basic')}
                example={examples('./Basic').default}
            />
            <Specs docs={docs[0]} />
            <Specs docs={docsPane[0]} title="StepBar.Step" />
        </DocPage>
    );
}

export default StepBar;
