import React from 'react';
import DocExample from 'components/DocExample';
import DocPage from 'components/DocPage';
import Specs from 'components/Specs';
import packageData from 'splunk-ui/components/TabBar/package.json';
import docs from '!!docgen-loader!splunk-ui/components/TabBar';
import docsTab from '!!docgen-loader!splunk-ui/components/TabBar/Tab';

const examples = require.context('splunk-ui/components/TabBar/examples/');
const examplesRaw = require.context('!!raw!splunk-ui/components/TabBar/examples/');

function TabBar() {
    return (
        <DocPage {...packageData} description={docs[0].description}>
            <DocExample
                title="Basic"
                description="Basic example."
                code={examplesRaw('./Basic')}
                example={examples('./Basic').default}
            />
            <DocExample
                title="Icons"
                description="Icon example."
                code={examplesRaw('./Icons')}
                example={examples('./Icons').default}
            />
            <Specs
                docs={docs[0]}
                title="TabBar"
            />
            <Specs
                docs={docsTab[0]}
                title="TabBar.Tab"
            />
        </DocPage>
    );
}

export default TabBar;
