import React from 'react';
import DocExample from 'components/DocExample';
import DocPage from 'components/DocPage';
import Specs from 'components/Specs';
import packageData from 'splunk-ui/components/TabLayout/package.json';
import docs from '!!docgen-loader!splunk-ui/components/TabLayout';
import docsPane from '!!docgen-loader!splunk-ui/components/TabLayout/Panel';

const examples = require.context('splunk-ui/components/TabLayout/examples/');
const examplesRaw = require.context('!!raw!splunk-ui/components/TabLayout/examples/');

function TabLayout() {
    return (
        <DocPage {...packageData} description={docs[0].description}>
            <DocExample
                title="Uncontrolled"
                description="In this example, the `TabLayout` is used as an uncontrolled component."
                code={examplesRaw('./Uncontrolled')}
                example={examples('./Uncontrolled').default}
            />
            <DocExample
                title="Controlled"
                description="In this example, the open panel is controlled by the parent component."
                code={examplesRaw('./Controlled')}
                example={examples('./Controlled').default}
            />
            <Specs docs={docs[0]} title="TabLayout" />
            <Specs docs={docsPane[0]} title="TabLayout.Panel" />
        </DocPage>
    );
}

export default TabLayout;
