import React from 'react';
import DocExample from 'components/DocExample';
import DocPage from 'components/DocPage';
import Specs from 'components/Specs';
import packageData from 'splunk-ui/components/Table/package.json';
import docs from '!!docgen-loader!splunk-ui/components/Table';
import docsHeadCell from '!!docgen-loader!splunk-ui/components/Table/HeadCell';
import docsRow from '!!docgen-loader!splunk-ui/components/Table/Row';

const examples = require.context('splunk-ui/components/Table/examples/');
const examplesRaw = require.context('!!raw!splunk-ui/components/Table/examples/');

function Table() {
    return (
        <DocPage {...packageData} description={docs[0].description}>
            <DocExample
                title="Basic"
                description="Basic example."
                code={examplesRaw('./Basic')}
                example={examples('./Basic').default}
            />
            <DocExample
                title="Docked Header"
                description="Dock the header against the window"
                code={examplesRaw('./DockedHeader')}
                example={examples('./DockedHeader').default}
            />
            <DocExample
                title="Fixed Header"
                description="Fix the header relative to the table"
                code={examplesRaw('./FixedHeader')}
                example={examples('./FixedHeader').default}
            />
            <DocExample
                title="Sortable Columns"
                description="If a column is provided a `sortKey`, it will be rendered as sortable.
                    When a sortable head cell is clicked, it will trigger an `onSort` callback. It
                    is the responsibility of the consumer to implement the sorting logic."
                code={examplesRaw('./SortableColumns')}
                example={examples('./SortableColumns').default}
            />
            <DocExample
                title="Expandable Rows"
                description="Rows that can be expanded to provide additional information"
                code={examplesRaw('./ExpandableRows')}
                example={examples('./ExpandableRows').default}
            />
            <Specs
                docs={docs[0]}
                title="Table"
            />
            <Specs
                docs={docsRow[0]}
                title="Table.Row"
            />
            <Specs
                docs={docsHeadCell[0]}
                title="Table.HeadCell"
            />
        </DocPage>
    );
}

export default Table;
