import React from 'react';
import DocExample from 'components/DocExample';
import DocPage from 'components/DocPage';
import Specs from 'components/Specs';
import packageData from 'splunk-ui/components/Tooltip/package.json';
import docs from '!!docgen-loader!splunk-ui/components/Tooltip';

const examples = require.context('splunk-ui/components/Tooltip/examples/');
const examplesRaw = require.context('!!raw!splunk-ui/components/Tooltip/examples/');

function Tooltip() {
    return (
        <DocPage {...packageData} description={docs[0].description}>
            <DocExample
                title="Basic"
                description="By default a superscript question mark is added."
                code={examplesRaw('./Basic')}
                example={examples('./Basic').default}
            />
            <DocExample
                title="Custom Content"
                description="Tooltip can also wrap a single node with custom content,
                note this will remove any other focus, blur, mouseenter and mouseleave handlers.
                If this is an issue, add an additional wrapper."
                code={examplesRaw('./CustomContent')}
                example={examples('./CustomContent').default}
            />
            <DocExample
                title="Controlled"
                description="Tooltips can also be controlled for more complex use cases.
                In this case, a 500ms delay has been added."
                code={examplesRaw('./Controlled')}
                example={examples('./Controlled').default}
            />
            <Specs docs={docs[0]} />
        </DocPage>
    );
}

export default Tooltip;
