import React from 'react';
import DocExample from 'components/DocExample';
import DocPage from 'components/DocPage';
import Specs from 'components/Specs';
import packageData from 'splunk-ui/components/WaitSpinner/package.json';
import docs from '!!docgen-loader!splunk-ui/components/WaitSpinner';

const examples = require.context('splunk-ui/components/WaitSpinner/examples/');
const examplesRaw = require.context('!!raw!splunk-ui/components/WaitSpinner/examples/');

function WaitSpinner() {
    return (
        <DocPage {...packageData} description={docs[0].description}>
            <DocExample
                title="Basic"
                code={examplesRaw('./Basic')}
                example={examples('./Basic').default}
            />
            <DocExample
                title="Medium Spinner"
                code={examplesRaw('./Medium')}
                example={examples('./Medium').default}
            />
            <DocExample
                title="Brand Color Spinner"
                code={examplesRaw('./Brand')}
                example={examples('./Brand').default}
            />

            <Specs docs={docs[0]} />
        </DocPage>
    );
}

export default WaitSpinner;
