import React, { Component } from 'react';
import { without } from 'lodash';
import Accordion from 'splunk-ui/components/Accordion';
import Button from 'splunk-ui/components/Button';
import Dropdown from 'splunk-ui/components/Dropdown';
import P from 'splunk-ui/components/Paragraph';

class InDropdown extends Component {
    constructor(...args) {
        super(...args);
        this.state = {
            openPanelId: 2,
        };
    }

    handleChange = (e, { panelId }) => {
        this.setState({ openPanelId: panelId });
    }

    render() {
        const { openPanelId } = this.state;
        const toggle = <Button label="Click Me" isMenu />;
        const closeReasons = without(Dropdown.possibleCloseReasons, 'contentClick');
        const accordionStyle = { width: 450 };
        const panelStyle = { padding: 20 };
        return (
            <Dropdown toggle={toggle} closeReasons={closeReasons}>
                <Accordion
                    style={accordionStyle}
                    openPanelId={openPanelId}
                    onChange={this.handleChange}
                >
                    <Accordion.Panel panelId={1} title="Panel 1">
                        <P style={panelStyle}>
                            Lorem ipsum dolor sit amet, consectetur adipisicing elit, sed do eiusmod
                            tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim
                            veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea
                            commodo consequat. Duis aute irure dolor in reprehenderit in voluptate
                            velit esse cillum dolore eu fugiat nulla pariatur. Excepteur sint
                            occaecat cupidatat non proident, sunt in culpa qui officia deserunt
                            mollit anim id est laborum.
                        </P>
                    </Accordion.Panel>
                    <Accordion.Panel panelId={2} title="Panel 2">
                        <P style={panelStyle}>
                            Lorem ipsum dolor sit amet, consectetur adipisicing elit, sed do eiusmod
                            tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim
                            veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea
                            commodo consequat. Duis aute irure dolor in reprehenderit in voluptate
                            velit esse cillum dolore eu fugiat nulla pariatur. Excepteur sint
                            occaecat cupidatat non proident, sunt in culpa qui officia deserunt
                            mollit anim id est laborum.
                        </P>
                    </Accordion.Panel>
                </Accordion>
            </Dropdown>
        );
    }
}

export default InDropdown;
