import React, { Component } from 'react';
import CollapsiblePanel from 'splunk-ui/components/CollapsiblePanel';
import P from 'splunk-ui/components/Paragraph';

class BasicControlled extends Component {
    constructor(...args) {
        super(...args);
        this.state = { open: false };
    }

    handleRequestClose = () => {
        this.setState({ open: false });
    }

    handleRequestOpen = () => {
        this.setState({ open: true });
    }

    render() {
        return (
            <CollapsiblePanel
                title="Basic Controlled Collapsible Panel"
                onRequestClose={this.handleRequestClose}
                onRequestOpen={this.handleRequestOpen}
                open={this.state.open}
            >
                <P style={{ padding: 10 }}>
                    Lorem ipsum dolor sit amet, consectetur adipisicing elit, sed do eiusmod tempor
                    incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis
                    nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat.
                    Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu
                    fugiat nulla pariatur. Excepteur sint occaecat cupidatat non proident, sunt in
                    culpa qui officia deserunt mollit anim id est laborum.
                </P>
            </CollapsiblePanel>
        );
    }
}

export default BasicControlled;
