import React, { Component } from 'react';
import ComboBox from 'splunk-ui/components/ComboBox';


class Example extends Component {
    constructor(props, ...rest) {
        super(props, ...rest);

        this.state = {
            value: '',
        };
    }

    handleChange = (e, { value }) => {
        this.setState({ value });
    }

    render() {
        return (
            <ComboBox inline value={this.state.value} onChange={this.handleChange}>
                <ComboBox.Option value="Up" description="Additional information..." />
                <ComboBox.Option value="Up and Down" />
                <ComboBox.Option value="Down" />
                <ComboBox.Option value="Down and Under" />
                <ComboBox.Option value="Left" />
                <ComboBox.Option value="Left and Over" />
                <ComboBox.Option value="Right" />
                <ComboBox.Option value="Right and Under" />
            </ComboBox>
        );
    }
}

export default Example;
