import React, { Component } from 'react';
import { includes } from 'lodash';
import ComboBox from 'splunk-ui/components/ComboBox';


class Example extends Component {
    constructor(props, ...rest) {
        super(props, ...rest);

        this.state = {
            value: '',
        };
    }

    handleChange = (e, { value }) => {
        this.setState({ value });
    }

    render() {
        const options = [
            'Up',
            'Up and Down',
            'Down',
            'Down and Under',
            'Left',
            'Left and Over',
            'Right',
            'Right and Under',
            'Eight',
            'Nine',
            'Ten',
            'Eleven',
        ].filter(option => includes(option, this.state.value))
        .map(option => <ComboBox.Option value={option} key={option} />);

        return (
            <ComboBox controlledFilter inline value={this.state.value} onChange={this.handleChange}>
                {options}
            </ComboBox>
        );
    }
}

export default Example;
