import React from 'react';
import ControlGroup from 'splunk-ui/components/ControlGroup';
import Date from 'splunk-ui/components/Date';
import Number from 'splunk-ui/components/Number';
import RadioBar from 'splunk-ui/components/RadioBar';
import Select from 'splunk-ui/components/Select';
import Text from 'splunk-ui/components/Text';

function Example() {
    return (
        <div>
            <ControlGroup
                label="A Text Field"
                help="Name cannot begin with an underscore."
                error
            >
                <Text canClear placeholder="optional" defaultValue="_name" error />
            </ControlGroup>

            <ControlGroup
                label="A Number Field"
                help="This field is required."
                error
            >
                <Number error />
            </ControlGroup>

            <ControlGroup
                label="A Date Field"
                help="This field is required."
                error
            >
                <Date error />
            </ControlGroup>

            <ControlGroup
                label="A Select Field"
                help="This field is required."
                error
            >
                <Select placeholder="Select..." error>
                    <Select.Option
                        label="Up"
                        value="1"
                    />
                    <Select.Option
                        label="Right"
                        value="2"
                    />
                </Select>
            </ControlGroup>

            <ControlGroup label="RadioBar Control" error>
                <RadioBar value={3} error>
                    <RadioBar.Option value={1} label="one" />
                    <RadioBar.Option value={2} label="two" />
                    <RadioBar.Option value={3} label="threethreethree" />
                    <RadioBar.Option value={4} label="four" />
                </RadioBar>
            </ControlGroup>
        </div>
    );
}

export default Example;
