import React from 'react';
import Button from 'splunk-ui/components/Button';
import Dropdown from 'splunk-ui/components/Dropdown';
import Menu from 'splunk-ui/components/Menu';

function Basic() {
    const toggle = <Button label="Direction" isMenu />;

    return (
        <Dropdown toggle={toggle}>
            <Menu style={{ width: 120 }}>
                <Menu.Item label="Up" />
                <Menu.Item label="Down" />
                <Menu.Item label="Left" />
                <Menu.Item label="Right" />
            </Menu>
        </Dropdown>
    );
}

export default Basic;
