import React from 'react';
import Button from 'splunk-ui/components/Button';
import Dropdown from 'splunk-ui/components/Dropdown';
import RotateCounter from 'splunk-ui/icons/RotateCounter';
import Star from 'splunk-ui/icons/Star';
import Menu from 'splunk-ui/components/Menu';

function Example() {
    const buttonToggle =
        <Button label="direction" appearance="pill" icon={<RotateCounter size={1.2} />} isMenu />;
    const iconToggle = <span style={{ fontSize: '50px' }}><Star /></span>;

    return (
        <div>
            <Dropdown toggle={buttonToggle} >
                <Menu style={{ width: 120 }}>
                    <Menu.Item label="Up" />
                    <Menu.Item label="Down" />
                    <Menu.Item label="Left" />
                    <Menu.Item label="Right" />
                </Menu>
            </Dropdown>
            <Dropdown toggle={iconToggle} style={{ marginLeft: '20px' }}>
                <Menu style={{ width: 120 }}>
                    <Menu.Item label="Up" />
                    <Menu.Item label="Down" />
                    <Menu.Item label="Left" />
                    <Menu.Item label="Right" />
                </Menu>
            </Dropdown>
        </div>
    );
}

export default Example;
